import './App.css';
import axios from 'axios';
import React, {useEffect, useState} from 'react';

const App = () => {


    const [drink, setDrink] = useState({})
    const [drinkName, setDrinkName] = useState('')
    const [drinkUrl, setDrinkUrl] = useState('')
    const [ingredients, setIngredients] = useState([])
    const [drinkMethod, setDrinkMethod] = useState('')

    const hook = () => {
            axios
                .get(`https://www.thecocktaildb.com/api/json/v1/1/random.php`)
                .then(response => {
                    console.log(response.data)
                    setDrinkName(response.data['drinks'][0]['strDrink'])
                    setDrinkUrl(response.data['drinks'][0]['strDrinkThumb'])
                    setDrinkMethod(response.data['drinks'][0]['strInstructions'])

                    console.log(Object.entries(response.data['drinks'][0]))

                    const transformIngredients = Object.entries(response.data['drinks'][0]).filter(([key, value]) => {
                        if (key.includes('strIngredient') && value?.length && value) {
                            return true
                        }
                    }).map(([key, value]) => {
                        return value
                    })

                    setIngredients(transformIngredients)
    })}

    useEffect(hook, [])



  return (
      <div>
          {drinkName}
          <img src={drinkUrl} alt="Picture of Cocktail"/>
          {ingredients.map(ingredient => <p>{ingredient}</p>)}
          {drinkMethod}
      </div>
  )
}

export default App;
