import styled from "styled-components";
import {themes} from "./Themes";

export const StyledNav = styled.nav`
  width: 100vw;
  background-color: black;
  color: ${themes.colorCodes.electricBlue};


  ul {
    display: flex;
    flex-direction: row;
    justify-content: flex-end;
    align-items: center;
    list-style-type: none;
    padding: 0;
    margin: 0;
  }
  
  ul li:first-child {
    order: 2;
    flex-basis: auto;
    margin: 0 auto;
  }
  
  ul li:nth-child(2),
  ul li:nth-child(3) {
    order: 1;
    padding: 1rem;
  }

  ul li:nth-child(4),
  ul li:nth-child(5),
  ul li:nth-child(6) {
    order: 3;
    padding: 1rem;
  }
`
