import React from 'react';



const SearchIngredient = ({ingredientChange, submitIngredientSearch}) => {
    const inputBtn = document.getElementsByClassName('input-btn');
    const userInput = document.getElementsByClassName('user-input');

    const clearInputField = () => {
        userInput.value = '';
    }
    return (
        <>

            <form onSubmit={submitIngredientSearch}>
                <h4>Search Ingredient</h4>
                <input className='user-input'
                    type="text"
                    onChange={ingredientChange}
                    required
                />
                <button type='submit' className='input-btn' onClick={clearInputField}>search</button>
            </form>
        </>
    );
}
export default SearchIngredient;
