import styled from 'styled-components';
import {themes} from "./Themes";

export const StyledLayout = styled.div`

  @import url('https://fonts.googleapis.com/css2?family=Charmonman&display=swap');
  
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  min-height: 100vh;
  width: 100vw;
  overflow-x: hidden;
  background-image: url('https://images.unsplash.com/photo-1566417713937-1c2732735804?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8OXx8ZHJpbmtzJTIwYXQlMjBiYXJ8ZW58MHwwfDB8YmxhY2tfYW5kX3doaXRlfA%3D%3D&auto=format&fit=crop&w=500&q=60');
  background-size: cover;
  color: ${themes.colorCodes.lightGrey};
        
  nav {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    height: 5rem;
    width: 100vw;
    background-color: ${themes.colorCodes.dark};
  }
  
  
  nav h1 {
    margin: 20px;
    font-family: 'Charmonman', cursive;
    font-size: 2rem;
  }
  
  .socialIcon {
    margin: 0 20px;
    cursor: pointer;
  }
  
  .socialIcon:hover {
    color: ${themes.colorCodes.lightPurple}
  }
  
  footer {
    height: 3rem;
    display: flex;
    flex-direction: row;
    justify-content: space-evenly;
    align-items: center;
    width: 100vw;
    background-color: ${themes.colorCodes.dark};
  }
`
