import styled from 'styled-components';
import {themes} from "./Themes";

export const StyledSearchForm = styled.div`
  color: var(--lightGrey);
  letter-spacing: 0.2rem;
  margin-bottom: 10px;
  
  button {
    margin-top: 10px;
    font-family: ${themes.fontFamilies.generalFont};
  }
  
  input {
    border-radius: 10px;
    height: 1.5rem;
    width: 70px;
    font-size: 24px;
  }
`
