import styled from 'styled-components';
import {themes} from "./Themes";

export const StyledErrorMsg = styled.div `
  :root {
    --lightPurple: #9246e0;
    --electricBlue: #1D40DC;
    --lightGrey: #adacb0;
    --generalFont: '"Montserrat", sans-serif';
  }
  
  h2 {color: var(--lightGrey);}
  
`
