import React from 'react';
import SearchButton from "./SearchButton";

const SearchName = ({nameChange, submitNameSearch}) => {
    return (
        <>
            <span>Search Cocktail Name</span>
            <form onSubmit={submitNameSearch}>
                <input
                    type="text"
                    onChange={nameChange}
                    required
                />
                <SearchButton />
            </form>
        </>
    );
}

export default SearchName;
