import React from 'react';
import {BrowserRouter, Route} from "react-router-dom";

const DisplayCocktail = ({drink: {name, url, method, ingredients}}) => {

    return (
        <BrowserRouter>
            <>
                <h3>{name}</h3>
                <img src={`${url}/preview`} alt="Image of Cocktail"/>
                <ul>
                    {
                        ingredients.map(ingredient => <li>{ingredient}</li>)
                    }
                </ul>
                <p>{method}</p>
            </>
            <Route></Route>
        </BrowserRouter>

    );
}

export default DisplayCocktail;
