import styled from 'styled-components';

export const StyledLayout = styled.div`
  height: 100vh;
  margin: 0;

        
  nav {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
  }
  
  nav h2 {
    margin: 10px 20px;
  }
  
  footer {
    text-align: center;
    height: 8%;
    display: flex;
    flex-direction: row;
    justify-content: space-around;
    align-items: center;
  }
`
