import styled from 'styled-components';
import {themes} from "./Themes";


export const StyledButtonRandomCocktail = styled.div`
    button {
      border-radius: 5px;
      //background: rgb(236,237,241);
      // background: linear-gradient(159deg, rgba(236,237,241,1) 0%, rgba(173,172,176,1) 99%);
      font-family: ${themes.fontFamilies.generalFont}, sans-serif;
      background-color: ${themes.colorCodes.electricBlue};
      margin-top: 15px;
      padding: 5px 10px 5px 10px;
      text-transform: uppercase;
      max-width: 90%;
      color: ${themes.colorCodes.lightGrey}
    }

    button:hover {
      cursor: pointer;
    }
`
