import React from 'react';

const DisplayCocktail = (props) => {

        return (
            <div>
                <h3>{props.name}</h3>
                <img src={`${props.url}/preview`} alt="Image of Cocktail"/>
                <ul>
                    {
                        props.ingredients.map(ingredient => <li>{ingredient}</li>)
                    }
                </ul>
                <p>{props.method}</p>
            </div>
        );
}

export default DisplayCocktail;
