import styled from 'styled-components';

export const StyledLayout = styled.div`
  //height: 100%;
  //margin: 0 auto;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;


        
  nav {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    height: 7%;
    width: 100vw;
  }
  
  nav h2 {
    margin: 10px 20px;
  }
  
  footer {
    //text-align: center;
    height: 7%;
    display: flex;
    flex-direction: row;
    justify-content: space-evenly;
    align-items: center;
    width: 100vw;
  }
`
