import styled from "styled-components";
import {themes} from "./Themes";

export const StyledNav = styled.nav`
  width: 100vw;
  max-height: 150px;
  background-color: black;

  ul {
    display: flex;
    flex-direction: row;
    justify-content: flex-end;
    align-items: center;
    list-style-type: none;
    padding: 0;
    margin: 0;
    color: ${themes.colorCodes.electricBlue};
  }
  
  li {
    display: block;
  }
  
  
  ul li:first-child {
    order: 2;
    flex-basis: auto;
    margin: 0 auto;
    color: ${themes.colorCodes.lightPurple};
    font-size: 1.8rem;
    font-family: ${themes.fontFamilies.accentFont};
  }
  
  ul li:nth-child(2),
  ul li:nth-child(3) {
    order: 1;
    padding: 1rem;
  }

  ul li:nth-child(4),
  ul li:nth-child(5),
  ul li:nth-child(6) {
    order: 3;
    padding: 1rem;
  }
  
  ul li:nth-child(6) {
    margin-right: 1rem;
  }
  
  .link {
    text-decoration: none;
    color: ${themes.colorCodes.electricBlue}
  }
  
  .link:hover {
      color: ${themes.colorCodes.lightPurple}
  }
  
`
