import './App.css';
import axios from 'axios';
import React, {useEffect, useState} from 'react';
import DisplayCocktail from "./components/DisplayCocktail";
import ButtonRandomCocktail from "./components/ButtonRandomCocktail";
import SearchIngredient from "./components/SearchIngredient";
import SearchName from "./components/SearchName";
import transformDrinkData from "./adaptors/getDrinks";


const App = () => {

    // STATE FOR COCKTAIL
    const [drinks, setDrinks] = useState([])

    // STATE FOR COCKTAIL INGREDIENT SEARCH
    const [searchIngredient, setSearchIngredient] = useState('')

    // STATE FOR COCKTAIL NAME SEARCH
    const [searchName, setSearchName] = useState('')


    const randomCocktailHandler = async () => {
        const {data} = await axios.get('https://www.thecocktaildb.com/api/json/v1/1/random.php')
        setDrinks([transformDrinkData(data)])
    }


    // SEARCH BY INGREDIENTS
    const handleIngredientChange = (e) => {
        setSearchIngredient(e.target.value)
    }


    const searchIngredients = (e) => {
        e.preventDefault()
        const ingredient = searchIngredient;
        const url = 'https://www.thecocktaildb.com/api/json/v1/1/filter.php?i='

        console.log(`${url}${ingredient}`)

        axios
            .get(`${url}${ingredient}`)
            .then(response => {
                console.log(response.data)
            })

        console.log('search ingredient was called')
    }


    // SEARCH BY COCKTAIL NAME
    const handleNameChange = (e) => {
        setSearchName(e.target.value)
    }

    const searchCocktailName = (e) => {
        e.preventDefault()
        const name = searchName
        const url = 'https://www.thecocktaildb.com/api/json/v1/1/search.php?s='

        axios
            .get(`${url}${name}`)
            .then(response => {

                const allDrinks = response.data['drinks']
                console.log(allDrinks)

                // console.log(allDrinks.map(drink => {
                //     transformDrinkData(drink)
                // }))


            })
    }


    if (drinks.length) {
        return (
            <div>
                <ButtonRandomCocktail randomCocktail={randomCocktailHandler}/>
                {
                    drinks.map(drink =>
                        <DisplayCocktail
                            drink={drink}
                            key={drink['strDrink']}
                        />
                    )
                }

            </div>
        )
    }

    return (
        <div>
            <ButtonRandomCocktail randomCocktail={randomCocktailHandler}/>
            <SearchIngredient ingredientChange={handleIngredientChange} submitIngredientSearch={searchIngredients}/>
            <SearchName nameChange={handleNameChange} submitNameSearch={searchCocktailName}/>
        </div>
    )
}

export default App;

/*
* to render conditionally. create state --> already done.
* by default, no drink is saved
*
* redirect ==> make child component of display cocktail route. Ternary operator using Redirect
*
*
* */
