const addToLocalStorage = (drinkToAdd) => {
    const drinkName = JSON.stringify(drinkToAdd.name)
    drinkToAdd.isFavouriteDrink = true
    localStorage.setItem(drinkName, JSON.stringify(drinkToAdd))
    console.log(localStorage)
}

const removeFromLocalStorage = (drinkToRemove) => {
    const drinkName = JSON.stringify(drinkToRemove.name)
    drinkToRemove.isFavouriteDrink = false
    localStorage.removeItem(drinkName)
    console.log(localStorage)
}


// when displaying saved drinks, need to change to true


