import styled from 'styled-components';

export const StyledLayout = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  min-height: 100vh;
  width: 100vw;

        
  nav {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    height: 2.5rem;
    width: 100vw;
  }
  
  nav h2 {
    margin: 10px 20px;
  }
  
  footer {
    height: 2.5rem;
    display: flex;
    flex-direction: row;
    justify-content: space-evenly;
    align-items: center;
    width: 100vw;
  }
`
