import React, { useContext, useState } from 'react';
import Favourite from '../media/isFavouriteHeart.png'
import NotFavourite from '../media/isNotFavouriteHeart.png'
import {DrinksContext} from "../state/DrinksContext"
import axios from "axios";
import transformDrinkData from "../adaptors/getDrinks";



const DisplayCocktail = ({drink: {name, url, method, ingredients, isFavouriteDrink}}) => {

    const {drinks, setDrinks} = useContext(DrinksContext)

    const [ingredientsToDisplay, setIngredientsToDisplay] = useState([])

    const [methodToDisplay, setMethodToDisplay] = useState('')

    const toggleFavourite = () => {
        const newDrinks = drinks.map(drink => {
            if (drink.name === name) {
                return {...drink, isFavouriteDrink: !isFavouriteDrink}
            }
            return drink
        } )
        setDrinks(newDrinks)
    }

    const showIngredients = () => {
        const url = 'https://www.thecocktaildb.com/api/json/v1/1/search.php?s='

        axios
            .get(`${url}${name}`)
            .then(response => {
                console.log(response.data)
                const allDrinks = response.data['drinks'].map(drink => transformDrinkData(drink))
                setIngredientsToDisplay(allDrinks[0].ingredients)
                setMethodToDisplay(allDrinks[0].method)
            }).catch(error => {
            console.log(`Sorry, we couldn't find ${name}`)
        })
    }




        if (ingredients.length) {
            return (
                <>
                    <h3>{name}</h3>
                    <img src={`${url}/preview`} alt="Image of Cocktail"/>
                    <ul>
                        {
                            ingredients.map(ingredient => <li key={ingredient}>{ingredient}</li>)
                        }
                    </ul>
                    <p>{method}</p>
                    <img src={isFavouriteDrink ? Favourite : NotFavourite} alt={isFavouriteDrink ? 'Icon Drink Favourited' : 'Icon Add to Favourites'} height='50px' onClick={toggleFavourite}/>
                </>
            )
        } else {
            return (
                <>
                    <h3>{name}</h3>
                    <img src={`${url}/preview`} alt="Image of Cocktail"/>
                    <button onClick={showIngredients}>Show me the details</button>
                    <ul>
                        {
                            ingredientsToDisplay.map(ingredient => <li key={ingredient}>{ingredient}</li>)
                        }
                    </ul>
                    <p>{methodToDisplay}</p>
                    <img src={isFavouriteDrink ? Favourite : NotFavourite} alt={isFavouriteDrink ? 'Icon Drink Favourited' : 'Icon Add to Favourites'} height='50px' onClick={toggleFavourite}/>
                </>
            )
        }

    }


export default DisplayCocktail;
