import React, {useContext} from 'react';
import DisplayCocktail from "./DisplayCocktail";
import {DrinksContext} from "../state/DrinksContext";
import {LoginContext} from "../state/LoginContext";

// does not work


const Favourites = () => {
    const {drinks, setDrinks} = useContext(DrinksContext)
    const {isLoggedIn, setIsLoggedIn} = useContext(LoginContext)

    if (isLoggedIn) {
        return (
            <>
                {
                    drinks.filter(drink => drinks.isLoggedIn).map(drink => <DisplayCocktail drink={drink} key={drink.name}/>)
                }
            </>
        );
    }

};

export default Favourites;
