import React from 'react';



const DisplayCocktail = ({drink: {name, url, method, ingredients, isFavouriteDrink}}) => {

    const isFavourite = isFavouriteDrink

    if (isFavourite) {
        return (
            <>
                <h3>{name}</h3>
                <img src={`${url}/preview`} alt="Image of Cocktail"/>
                <ul>
                    {
                        ingredients.map(ingredient => <li key={ingredient}>{ingredient}</li>)
                    }
                </ul>
                <p>{method}</p>
                <img src="../media/isFavouriteHeart.png" alt=""/>
            </>
        )
    } else {
        return (
            <>
                <h3>{name}</h3>
                <img src={`${url}/preview`} alt="Image of Cocktail"/>
                <ul>
                    {
                        ingredients.map(ingredient => <li key={ingredient}>{ingredient}</li>)
                    }
                </ul>
                <p>{method}</p>
                <img src="../media/isNotFavouriteHeart.png" alt=""/>
            </>
        )
    }


    }


export default DisplayCocktail;
