import React from 'react';


const DisplayCocktail = ({drink: {name, url, method, ingredients}, setFavouriteDrinks}) => {

    return (

            <>
                <h3>{name}</h3>
                <img src={`${url}/preview`} alt="Image of Cocktail"/>
                <ul>
                    {
                        ingredients.map(ingredient => <li key={ingredient}>{ingredient}</li>)
                    }
                </ul>
                <p>{method}</p>
                <p>Add To Favourites <input type="radio"/></p>
            </>
    );
}
export default DisplayCocktail;
