import './App.css';
import axios from 'axios';
import React, {useEffect, useState} from 'react';
import DisplayRandomCocktail from "./components/DisplayRandomCocktail";
import ButtonRandomCocktail from "./components/ButtonRandomCocktail";
import SearchIngredient from "./components/SearchIngredient";
import SearchName from "./components/SearchName";

const App = () => {

    // STATE FOR RANDOM COCKTAIL
    const [drinkName, setDrinkName] = useState('')
    const [drinkUrl, setDrinkUrl] = useState('')
    const [ingredients, setIngredients] = useState([])
    const [drinkMethod, setDrinkMethod] = useState('')

    // STATE FOR COCKTAIL INGREDIENT SEARCH
    const [searchIngredient, setSearchIngredient] = useState('')

    // STATE FOR COCKTAIL NAME SEARCH
    const [searchName, setSearchName] = useState('')


    const randomCocktail = () => {
            axios
                .get(`https://www.thecocktaildb.com/api/json/v1/1/random.php`)
                .then(response => {
                    console.log(response.data)
                    setDrinkName(response.data['drinks'][0]['strDrink'])
                    setDrinkUrl(response.data['drinks'][0]['strDrinkThumb'])
                    setDrinkMethod(response.data['drinks'][0]['strInstructions'])
                    const transformIngredients = Object.entries(response.data['drinks'][0]).filter(([key, value]) => {
                        if (key.includes('strIngredient') && value?.length && value) {
                            return true
                        }
                    }).map(([key, value]) => {
                        return value
                    })
                    setIngredients(transformIngredients)
    })}

    // SEARCH BY INGREDIENTS
    const handleIngredientChange = (e) => {
        setSearchIngredient(e.target.value)
    }

    const searchIngredients = (e) => {
        e.preventDefault()
        console.log(searchIngredient)
        const ingredient = searchIngredient;
        const url = 'https://www.thecocktaildb.com/api/json/v1/1/filter.php?i='

        console.log(`${url}${ingredient}`)

        axios
            .get(`${url}${ingredient}`)
            .then(response => {
                console.log(response.data)
            })

        console.log('search ingredient was called')
    }


    // SEARCH BY NAME
    const handleNameChange = (e) => {
        setSearchName(e.target.value)
    }

    const searchCocktailName = (e) => {
        e.preventDefault()
        console.log(searchName)
        const name = searchName
        const url = 'https://www.thecocktaildb.com/api/json/v1/1/search.php?s='

        console.log(`${url}${name}`)

        axios
            .get(`${url}${name}`)
            .then(response => {
                console.log(response.data)
            })

        console.log('search name was called')
    }




  if (drinkUrl !== '') {
      return (
          <div>
              <ButtonRandomCocktail randomCocktail={randomCocktail}/>
              <DisplayRandomCocktail
                  name={drinkName}
                  url={drinkUrl}
                  ingredients={ingredients}
                  method={drinkMethod}
              />
          </div>
      )
  }

  return (
      <div>
          <ButtonRandomCocktail randomCocktail={randomCocktail}/>
          <SearchIngredient ingredientChange={handleIngredientChange} sumbitIngredientSearch={searchIngredients}/>
          <SearchName nameChange={handleNameChange} submitNameSearch={searchCocktailName}/>
      </div>
  )
}

export default App;

