import styled from 'styled-components';

export const StyledLayout = styled.div`
  height: 100%;
  margin: 0;

        
  nav {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    height: 7%;
  }
  
  nav h2 {
    margin: 10px 20px;
  }
  
  footer {
    //text-align: center;
    //height: 7%;
    //display: flex;
    //flex-direction: row;
    //justify-content: space-evenly;
    //align-items: center;
    //position: absolute;
    //bottom: 0;
    
    position: fixed;
    bottom: 0;
    left: 0;
    width: 100%;
    text-align: center;
  }
`
